import React from "react";
import { styled } from "styled-components";

const Header = ({  toggleDarkMode, userDetail }) => {
  return (
    <HeaderStyled>
      <div className="logo">Task Manager</div>
      <nav className="nav-links">
        <a href="/">Home</a>
       {userDetail ? (<p>{`Welcome ${userDetail}`}</p>) : (<>
        <a href="/login">Sign In</a>
        <a href="/signup">Sign Up</a>
       </> )}           


      </nav>
    </HeaderStyled>
  );
};

export default Header;

const HeaderStyled = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  background: teal;
  color: #fff;

  .logo {
    font-size: 1.5rem;
    font-weight: bold;
  }

  .nav-links {
    display: flex;
    gap: 1.5rem;

    a {
      color: #fff;
      text-decoration: none;
      font-weight: 500;
      transition: 0.3s;

      &:hover {
        color: #000;
      }
    }
  }

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;

    .nav-links {
      flex-direction: column;
      gap: 0.5rem;
    }
  }
`;
